:w1 ld4l:hasPreferredTitle :title1 ;
:title1 a madsrdf:Title ;
    rdfs:label “[subfields concatenated if exists: a,b,f,g,k,n,p,v]” ;
    dcterms:language [Lexvo URI for resource language code in 008 or 046] ;
    dcterms:hasPart :titlePart .
:titlePart a madsrdf:NonSortElement ;
    rdfs:label "value of rdfs:label from 0 to n (value of ind2)" .

:title1 a madsrdf:Title ;
    ...
    dcterms:hasPart :titlePart1 .
:titlePart1 a madsrdf:MainTitleElement ;
    rdfs:label "value of subfield a" .

:title1 a madsrdf:Title ;
    ...
    dcterms:hasPart :titlepart2 .
:titlePart2 a madsrdf:SubtitleElement ;
    rdfs:label "[subfield b substring after : and before next : ; =]" ;
    ld4l:rank "[order subfield b substrings after : and before next ; = or EOL as appear]"@xsd:integer.

:w1 ld4l:hasTitle :title2 .
:title2 a madsrdf:Title ;
    rdfs:label "[subfield b substring after ; and before next ; = or EOL]" ;
    ld4l:rank "[order subfield b substrings after ; or = as appear]"@xsd:integer ;
    dcterms:hasPart :titlePart1, :titlePart2 .
:titlePart1 a madsrdf:MainTitleElement ;
    rdfs:label "[subfield b substring after ; and before next : ; = or EOL - including subtitles in this substring]" .
:titlePart2 a madsrdf:SubtitleElement ; #if : appears after this ; preceded substring and before next ; = or EOL
    rdfs:label "[subfield b substring after : and before next : ; =]" ;
    ld4l:rank "[order subfield b substrings after : and before next ; = or EOL as appear]"@xsd:integer.

:w1 ld4l:hasTitle :title2 .
:title2 a madsrdf:ParallelTitle ;
    rdfs:label "[subfield b substring after = and before ; = or EOL]" ;
    ld4l:rank "[order subfield b substrings after ; or = as appear]"@xsd:integer ;
    dcterms:hasPart :titlePart1, :titlePart2 .
:titlePart1 a madsrdf:MainTitleElement ;
    rdfs:label "[subfield b substring after = and before next ; = or EOL - including subtitles in this substring]" .
:titlePart2 a madsrdf:SubtitleElement ; #if :: appears after this = preceded substring and before next ; = or EOL
    rdfs:label "[subfield b substring after : and before next : ; =]" ;
    ld4l:rank "[order subfield b substrings after : and before next ; = or EOL as appear]"@xsd:integer.

:title1 a madsrdf:Title ;
    ...
    dcterms:hasPart :titlePart1 .
:titlePart1 a madsrdf:MainTitleElement ;
    rdfs:label "value of subfield k" .

