w:1 bf:identifiedBy :id1 .
    :id1 a bf:Identifier ;
    bf:source “National Union Catalog of Manuscript Collections” ;
    rdf:value “[field value]” .

:w1 bf:identifiedBy :id1 .
    :id1 a bf:Identifier ;
    bf:source “[MARC Org code value contained in parentheses]” ;
    rdf:value “[field value after parenthesis]” .

:i1 bf:classification :class 1 . 
:class1 a bf:ClassificationLcc ;
    bf:classificationPortion "[subfield a field value]" ;
    bf:itemPortion "[field value]" .

bf:Work 
    ld4l:hasPreferredTitle :title1 ;
:title1 a madsrdf:Title ;
    rdfs:label “[field value concatenated with subfield b value if exists]” ;
    dcterms:language [Lexvo URI for resource language code in 008] .

bf:Work 
    ld4l:hasPreferredTitle :title1 ;
:title1 a madsrdf:Title ;
    rdfs:label “[field value concatenated after subfield a value]” ;
    dcterms:hasPart :titlepart1 ;
    dcterms:language [Lexvo URI for resource language code in 008] .

:titlepart1 a madsrdf:SubtitleElement ;
    rdfs:label "[field value]" ;
    vivo:rank "[order appeared in 245 integer]"@xsd:integer .

:in1 a ld4l:PublicationActivity ;
    bf:place :place1 .
:place1 a prov:Location ;
    rdfs:label "value of subfield" .

:in1 a ld4l:PublicationActivity ;
    bf:agent :agent1 .
:agent1 a foaf:Agent ;
    rdfs:label "value of subfield" .

:in1 bf:extent :ext1 .
:ext1 a bf:Extent ;
    bf:unit :unit1 ;
    rdfs:label "value of subfield separate from units" .
:unit1 a bf:Unit, bibo:Page ;
    rdfs:label "value of subfield units normalized" .
